mapper.update:
UPDATE interview
title = #{title}, position = #{position}, company = #{company}, interview_type = #{interviewType}, difficulty_level = #{difficultyLevel}, status = #{status}, total_questions = #{totalQuestions}, answered_questions = #{answeredQuestions}, overall_score = #{overallScore}, start_time = #{startTime}, end_time = #{endTime}, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT * FROM interview

    AND user_id = #{userId}
    AND status = #{status}
    AND position LIKE CONCAT('%', #{position}, '%')
    AND company LIKE CONCAT('%', #{company}, '%')
    AND interview_type = #{interviewType}
    AND difficulty_level = #{difficultyLevel}

ORDER BY updated_at DESC
