mapper.select:
SELECT * FROM interview_reports


        AND session_id = #{sessionId}


        AND id = #{id}

mapper.update:
UPDATE interview_reports
overall_score = #{overallScore}, professional_score = #{professionalScore}, logic_score = #{logicScore}, completeness_score = #{completenessScore}, performance_analysis = #{performanceAnalysis}, skill_assessment = #{skillAssessment}, improvement_suggestions = #{improvementSuggestions}, strong_points = #{strongPoints}, weak_points = #{weakPoints}, updated_at = #{updatedAt}
WHERE id = #{id}
